import time

class helpers:
    def __init__(self):
        self.clock_tot = 0
        self.num_tot = 0
        self.clock_tot_2 = 0
        self.num_tot_2 = 0
        self.clock_tot_3 = 0
        self.num_tot_3 = 0

    def clocker(self, func):
        def new_func(*arg, **kwargs):
            clock_start = time.time()
            val = func(*arg, **kwargs)
            clock_end = time.time()
            self.clock_tot += clock_end-clock_start
            self.num_tot += 1
            return val
        return new_func
    
    def clocker_2(self, func):
        def new_func(*arg, **kwargs):
            clock_start = time.time()
            val = func(*arg, **kwargs)
            clock_end = time.time()
            self.clock_tot_2 += clock_end-clock_start
            self.num_tot_2 += 1
            return val
        return new_func
    
    def clocker_3(self, func):
        def new_func(*arg, **kwargs):
            clock_start = time.time()
            val = func(*arg, **kwargs)
            clock_end = time.time()
            self.clock_tot_3 += clock_end-clock_start
            self.num_tot_3 += 1
            return val
        return new_func

    def log_printer(self, func):
        def new_func(*arg, **kwargs):
            val = func(*arg, **kwargs)
            return val
        return new_func


helper = helpers()